/// <reference types="mongoose" />
import { Populate, MaybeItem } from './types';
declare module 'mongoose' {
    interface DocumentQuery<T, DocType extends Document, QueryHelpers = {}> extends mquery {
        populateTs<P extends keyof MaybeItem<NonNullable<T>>>(prop: P): DocumentQuery<Populate<T, P>, T & Document> & QueryHelpers;
    }
}
